assign inv_gated_en = inv_all || !one_fsm_idle
                   || inv_one || !all_fsm_idle;

gated_clk_cell  x_gated_invclk_cell (
  .clk_in               (cpuclk              ),
  .clk_out              (inv_clk             ),
  .external_en          (1'b0                ),
