output          lf_reqarb_last;        
output          lf_reqarb_req;         
output          lf_reqarb_tag_tee_deny; 
output          lf_reqarb_tee;         
output          lf_xx_busy;            


reg     [29:0]  lf_buf_addr;           
reg     [3 :0]  lf_buf_ffb;            
reg     [3 :0]  lf_buf_prot;           
reg             lf_buf_tag_tee_deny;   
reg     [1 :0]  lf_cnt;                
reg             lf_cur_st;             
reg             lf_nxt_st;             
reg             rfl_cur_st;            
reg     [31:0]  rfl_data;              
reg             rfl_error;             
reg     [1 :0]  rfl_low;               
reg             rfl_nxt_st;            
reg             rfl_tee;               


wire    [29:0]  acc_addr;              
wire    [3 :0]  acc_ffb;               
wire    [29:0]  acc_lf_addr;           
wire    [3 :0]  acc_lf_ffb;            
wire            acc_lf_req;            
wire            acc_lf_tee_deny;       
wire    [3 :0]  acc_prot;              
wire            acc_req;               
wire            biuif_cmplt;           
wire            biuif_data_vld;        
wire            biuif_error;           
wire            biuif_grant;           
wire            biuif_lf_cmplt;        
wire            biuif_lf_data_vld;     
wire            biuif_lf_error;        
wire            biuif_lf_grant;        
wire    [31:0]  biuif_lf_rdata;        
wire    [31:0]  biuif_rdata;           
wire            biuif_tee;             
wire            cache_cp0_lpmd_ack;    
wire            cache_cp0_srst_ack;    
wire            cache_global_en;       
wire            cache_iu_lockup_ack;   
wire            cp0_cache_lpmd_req;    
wire            cp0_cache_srst_req;    
wire            cpuclk;                
wire            cpurst_b;              
wire            iu_cache_lockup_req;   
wire    [29:0]  lf_acc_addr;           
wire            lf_acc_buf_clr;        
wire    [31:0]  lf_acc_bypass_data;    
wire            lf_acc_bypass_vld;     
wire            lf_acc_error;          
wire    [3 :0]  lf_acc_prot;           
wire    [31:0]  lf_addr;               
wire    [29:0]  lf_base_addr;          
wire    [31:0]  lf_biuif_addr;         
wire    [3 :0]  lf_biuif_prot;         
wire            lf_biuif_req;          
wire            lf_buf_clk;            
wire            lf_buf_gated_en;       
wire            lf_busy;               
wire            lf_bypass_hit;         
wire            lf_bypass_vld;         
wire            lf_cmplt;              
wire            lf_cnt_done;           
