assign decd_inst_icpa  = decd_op[6:0] == 7'b0001011 &&  decd_inst[25];
assign decd_inst_nop   = decd_op[4:0] == 5'b1 && decd_rd[4:0] == 5'b0 &&
                         decd_imm[5:0] == 6'b0;
